package module2;

public class MathDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.sqrt(144));
		System.out.println(Math.abs(-66));
		System.out.println(Math.min(33, 44));
		System.out.println(Math.max(33, 44));
		System.out.println(Math.random());
		
		double d = (int)(Math.random() * 100);
		System.out.println(d);
		System.out.println(Math.round(899.6));
		
		System.out.println(Math.ceil(878.12));
		System.out.println(Math.floor(878.666));
		System.out.println(Math.TAU);
		System.out.println(Math.PI);
		System.out.println(Math.pow(5, 3));
		System.out.println(Math.getExponent(758.25));
		System.out.println(Math.abs(87.21));
		System.out.println(Math.atan(9798.1564));
	}

}
